import React from "react";
import "./Blog.css";
import Blog1 from './img/blog-01.jpg';
import Blog2 from './img/blog-02.jpg';
import Blog3 from './img/blog-03.jpg';

function Blog() {
  return (
    <div className="blog component__space" id="Blog">
      <div className="heading">
        <h1 className="heading">Latest News</h1>
        <p className="heading p__color">
          As a civil engineer, I keep myself updated with the latest developments,
        </p>
        <p className="heading p__color">
          and here are some of the recent news and articles in the field.
        </p>
      </div>
      <div className="container">
        <div className="row">
          <div className="col__3">
            <div className="project__box pointer relative">
              <div className="project__box__img pointer relative">
                <div className="project__img__box">
                  <img src={Blog1} alt="" className="project__img" />
                </div>
                <div className="mask__effect"></div>
              </div>
              <div className="Blog__meta absolute">
                <h5 className="project__text">Infrastructure</h5>
                <h4 className="project__text">Innovations in urban infrastructure</h4>
                <a href="#" className="blog project__btn btn">Read More</a>
              </div>
            </div>
          </div>

          <div className="col__3">
            <div className="project__box pointer relative">
              <div className="project__box__img pointer relative">
                <div className="project__img__box">
                  <img src={Blog2} alt="" className="project__img" />
                </div>
                <div className="mask__effect"></div>
              </div>
              <div className="Blog__meta absolute">
                <h5 className="project__text">Sustainability</h5>
                <h4 className="project__text">Sustainable construction practices</h4>
                <a href="#" className="blog project__btn btn">Read More</a>
              </div>
            </div>
          </div>

          <div className="col__3">
            <div className="project__box pointer relative">
              <div className="project__box__img pointer relative">
                <div className="project__img__box">
                  <img src={Blog3} alt="" className="project__img" />
                </div>
                <div className="mask__effect"></div>
              </div>
              <div className="Blog__meta absolute">
                <h5 className="project__text">Technology</h5>
                <h4 className="project__text">Advancements in construction technology</h4>
                <a href="#" className="blog project__btn btn">Read More</a>
              </div>
            </div>
          </div>

        </div>
      </div>
    </div>
  );
}

export default Blog;
